import { Template } from 'meteor/templating';
import { ReactiveVar } from 'meteor/reactive-var';

import './main.html';

Template.hello.onCreated(function helloOnCreated() {

  this.points= new ReactiveVar([]);
});

Template.hello.helpers({
  points: function(){
    return Template.instance().points.get();
  },
});

Template.hello.events({
  'click button'(event, instance) {
    r = $("#radius").val();
    counter = 1;
    var x0=0;
    var y0=r;
    var p0 = 1-r;
    var x=x0;
    var p = p0;
    var y=y0;
    var foo = Template.instance().points.get(0);
    Template.instance().points.get(0).set({xp: x0, yp: y0, pv: p});

    while(x<y){
      if(p < 0){
        x+=1;
        p+= 2*x +1;
        var foo = Template.instance().points.get();
        Template.instance().points.get(counter).set({xp: x, yp: y, pv: p});
        counter +=1;

      }else{
        x+=1;
        y-=1;
        p+= 2*x +1 - 2*y;
        var foo = Template.instance().points.get(counter);
        Template.instance().points.get().push({xp: x, yp: y, pv: p});
        counter +=1;
      }
    }
         console.log(Template.instance().points.get());
},
});
