import { Template } from 'meteor/templating';
import { ReactiveVar } from 'meteor/reactive-var';

import './main.html';

Template.hello.onCreated(function helloOnCreated() {
  // counter starts at 0
  this.counter = new ReactiveVar(0);
  this.Template.instance().points= new ReactiveVar([]);
});

Template.hello.helpers({
  points() {
    return Template.instance().Template.instance().points.get();
  },
});

Template.hello.events({
  'click button'(event, instance) {
    r = $("#radius").val();
    counter = 1;
    var x0=0;
    var y0=r;
    var p0 = 1-r;
    var x=x0;
    var p = p0;
    var y=y0;
      Template.instance().matches[0].x = x0;
      Template.instance().points[0].y = y0;
      Template.instance().points[0].p = p;
    while(x<y){
      if(p < 0){
        x+=1;
        p+= 2*x +1;
        Template.instance().points[counter].x = x;
        Template.instance().points[counter].y  = y;
        Template.instance().points[counter].p = p;
        counter +=1;
         console.log(x,y,p);
      }else{
        x+=1;
        y-=1;
        p+= 2*x +1 - 2*y;
        Template.instance().points[counter].x = x;
        Template.instance().points[counter].y = y;
        Template.instance().points[counter].p = p;
        counter +=1;
        console.log(x,y,p);
      }
    }
},
});
